import React, { useState, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Box,
} from '@mui/material';
import { categoriesAPI } from '../services/api';

export default function TaskForm({ open, onClose, onSubmit, task, categories,setCategories }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [categoryId, setCategoryId] = useState('');

  const handleCategoryChange = async (e) => {
  const value = e.target.value;

  if (value === '__add_new__') {
    const newName = window.prompt('Enter new category name:');
    if (!newName) return;

    try {
      const res = await categoriesAPI.createCategory(newName);
      const newCategory = res.data.category;

      // Update categories in parent
      setCategories((prev) => [...prev, newCategory]);

      setCategoryId(newCategory.id);
    } catch (err) {
      alert('Failed to create category');
    }
  } else {
    setCategoryId(value);
  }
};

  useEffect(() => {
    if (task) {
      setTitle(task.title);
      setDescription(task.description || '');
      setDueDate(task.due_date || '');
      setCategoryId(task.category_id);
    } else {
      setTitle('');
      setDescription('');
      setDueDate('');
      setCategoryId('');
    }
  }, [task, open]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title || !categoryId) return;

    onSubmit({
      title,
      description: description || undefined,
      due_date: dueDate || undefined,
      category_id: categoryId,
      completed: task?.completed || false,
    });

    onClose();
  };

  return (
    <Dialog open={open} onClose={onClose} maxWidth="sm" fullWidth>
      <DialogTitle>{task ? 'Edit Task' : 'Create New Task'}</DialogTitle>
      <DialogContent>
        <Box component="form" onSubmit={handleSubmit} sx={{ pt: 1 }}>
          <TextField
            fullWidth
            label="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            margin="normal"
            required
          />

          <TextField
            fullWidth
            label="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            margin="normal"
            multiline
            rows={3}
          />

          <TextField
            fullWidth
            label="Due Date"
            type="date"
            value={dueDate}
            onChange={(e) => setDueDate(e.target.value)}
            margin="normal"
            InputLabelProps={{ shrink: true }}
          />
            <TextField
            select
            label="Priority"
            value={priority}
            onChange={(e) => setPriority(e.target.value)}
            fullWidth
            >
            {['High', 'Medium', 'Low'].map((level) => (
                <MenuItem key={level} value={level}>
                {level}
                </MenuItem>
            ))}
            </TextField>

          <FormControl fullWidth margin="normal" required>
            <InputLabel>Category</InputLabel>
            <Select
              value={categoryId}
            onChange={handleCategoryChange}
              label="Category"
            >
              {categories.map((category) => (
                <MenuItem key={category.id} value={category.id}>
                    {category.name}
                </MenuItem>
                ))}
                <MenuItem value="__add_new__" sx={{ fontStyle: 'italic', color: 'primary.main' }}>
                + Add new category
                </MenuItem>

            </Select>
          </FormControl>
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button onClick={handleSubmit} variant="contained">
          {task ? 'Update' : 'Create'}
        </Button>
      </DialogActions>
    </Dialog>
  );
}
