import React from 'react';
import {
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Checkbox,
  Chip,
  Typography,
  Box,
  Paper,
} from '@mui/material';
import { Edit, Delete } from '@mui/icons-material';

export default function TaskList({ tasks, onToggleComplete, onEdit, onDelete }) {
  if (tasks.length === 0) {
    return (
      <Paper sx={{ p: 3, textAlign: 'center' }}>
        <Typography variant="h6" color="text.secondary">
          No tasks found
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Create your first task to get started!
        </Typography>
      </Paper>
    );
  }

  return (
    <List>
      {tasks.map((task) => (
        <Paper key={task.id} sx={{ mb: 1 }}>
          <ListItem>
            <Checkbox
              checked={task.completed}
              onChange={() => onToggleComplete(task)}
              sx={{ mr: 1 }}
            />
            <ListItemText
              primary={
                <Typography
                  variant="h6"
                  sx={{
                    textDecoration: task.completed ? 'line-through' : 'none',
                    color: task.completed ? 'text.secondary' : 'text.primary',
                  }}
                >
                  {task.title}
                </Typography>
              }
              secondary={
                <Box>
                  {task.description && (
                    <Typography variant="body2" sx={{ mb: 1 }}>
                      {task.description}
                    </Typography>
                  )}
                  <Box sx={{ display: 'flex', gap: 1, alignItems: 'center' }}>
                    <Chip
                      label={task.category_name}
                      size="small"
                      color="primary"
                      variant="outlined"
                    />
                    {task.due_date && (
                      <Chip
                        label={`Due: ${new Date(task.due_date).toLocaleDateString()}`}
                        size="small"
                        color={new Date(task.due_date) < new Date() ? 'error' : 'default'}
                        variant="outlined"
                      />
                    )}
                  </Box>
                </Box>
              }
            />
            <ListItemSecondaryAction>
              <IconButton onClick={() => onEdit(task)} size="small">
                <Edit />
              </IconButton>
              <IconButton onClick={() => onDelete(task)} size="small" color="error">
                <Delete />
              </IconButton>
            </ListItemSecondaryAction>
          </ListItem>
        </Paper>
      ))}
    </List>
  );
}
